import React, { Component } from 'react';
import axios from "axios"
// import Signin from "./Signin"
 import { Link } from 'react-router-dom';

class Search extends Component {

    constructor(props){
      super(props)
       this.state = { 
         movies: [],
         search: "",
         input: "",
         comment:""
        }
    }


  componentDidMount() {

    axios('http://localhost:5000/search', {withCredentials: true})
     .then(result => {

       // console.log("dfdfd", result)
       this.setState({movies: result.data.movies}, () =>{
         console.log(this.state)
       })
     })  
     .catch((error)=> {

       console.log(error)
     })
  }


    handleChange = (e)=> {
        this.setState({input: e.target.value})  // this helps you target the click target to search

      }

      handleSubmit = (e)=> {

        var input = this.state.input

        this.setState({search: input})
      }

      logInHandler = (e)=> {
        this.setState({isLoggedIn: true})
      }


      handleCommentSubmit = (filmId, e)=>{
        e.preventDefault();
        console.log(filmId)
        let data = {
            filmId:"",
            userId: this.props.userId,
            comment:this.state.comment
        }

        axios.post(`http://localhost:5000/comments`,data).then(result =>{
          console.log("He", result)
        }).catch(err => console.log(err)) 


      }

  render() {
     // debugger
    if(this.props.isLoggedIn) {
        var allMoviesSearch  = this.state.movies.filter( (movies) => {
        let moviesNameToLowerCase = movies.title.toLowerCase();
        let searchName = this.state.search.toLowerCase();
        return (moviesNameToLowerCase.includes(searchName))
        })

        var  showFilm = allMoviesSearch.map( oneFilm => {
          console.log(oneFilm)
            return( 
              <div>
                <ul>
                    <li>Movie title:{oneFilm.title}</li>
                    <li>Movie director:{oneFilm.director}</li>
                    <li>Movie year:{oneFilm.year}</li>
                    <li>Movie duration:{oneFilm.duration}</li>
                    <li>Comments: {oneFilm.comments}</li>
                </ul>
                <form onSubmit={e =>{this.handleCommentSubmit(oneFilm._id, e)}}>
                  <input type="text" placeholder="leave a comment" onChange={this.handleChange}/>
                  <button type="submit">Comment</button>
                </form>
                </div>
            )
        })

        return (
            <div>
                <div>
                    <input type="text" name="search" onChange={this.handleChange} ></input>
                    <button onClick={this.handleSubmit} value="Submit"></button>   
                </div>
            {this.state.search === "" ? <div></div>: <div>{showFilm} Rate this movie  </div>}
            </div>
        )
    } else {
       // debugger
        return <p><Link to="/Signin">Sign-in</Link></p> 

    }
  }    
} 


export default Search;
