import React, { Component } from 'react';
import axios from "axios";
import { Link, Redirect } from 'react-router-dom';
import './Signin.css';
// import './images'


axios.defaults.withCredentials = true;
class Signin extends Component {
    constructor(){
        super()
        this.state = { 
            username: '',
            password: '',

        }

    }
    handleSigninSubmit = (e) => {

        e.preventDefault();

        axios.post(`http://localhost:5000/Signin`, 
        {   
            withCredentials: true,
            method: 'post',
            data: {
                username: this.state.username,
                password: this.state.password,
            }
        })
        .then((result)=> { 
            console.log(result)
            this.props.logInHandler()
        })
        .catch((err)=>{
        //  
            console.log(err)
        })

    }




    handleOnChange = (e) => {
        var objectData = {}
        objectData[e.target.name] = e.target.value
        this.setState(objectData)

    }

 render() {
    return ( 

        <div className="limiter">
		<div className="container-login100">
			<div className="wrap-login100">
				<div className="login100-pic js-tilt" data-tilt>
					<img src="images/img-01.png" alt="IMG" />
				</div>

				<form className="login100-form validate-form">
					<span className="login100-form-title">
						Member Login
					</span>

					<div className="wrap-input100 validate-input" data-validate = "Valid email is required: ex@abc.xyz">
						{/* <input className="input100" type="text" name="email" placeholder="Email"/> */}

                    <form onSubmit={this.handleSigninSubmit}>
        //              <input  onChange={this.handleOnChange} name="username" type="text" placeholder="username"/>
        //              <input  onChange={this.handleOnChange} name="password"  type="password" placholder="password"/>
        //              <input type="submit" value='submit'/>
        //          </form>

						<span className="focus-input100"></span>
						<span className="symbol-input100">
							<i className="fa fa-envelope" aria-hidden="true"></i>
						</span>
					</div>

					<div className="wrap-input100 validate-input" data-validate = "Password is required">
						<input className="input100" type="password" name="pass" placeholder="Password"/>
						<span className="focus-input100"></span>
						<span className="symbol-input100">
							<i className="fa fa-lock" aria-hidden="true"></i>
						</span>
					</div>

					<div className="container-login100-form-btn">
						<button className="login100-form-btn">
							Login
						</button>
					</div>

					<div className="text-center p-t-12">
						<span className="txt1">
							Forgot
						</span>
						<a className="txt2">
							Username / Password?
						</a>
					</div>

					<div className="text-center p-t-136">
						<a className="txt2" href="#">
							Create your Account
							<i className="fa fa-long-arrow-right m-l-5" aria-hidden="true"></i>
						</a>
					</div>
				</form>
			</div>
		</div>
	</div>


        //   <div>

        //      <h3>Sign in </h3>
        //          <form onSubmit={this.handleSigninSubmit}>
        //              <input onChange={this.handleOnChange} name="username" type="text" placeholder="username"/>
        //              <input onChange={this.handleOnChange} name="password"  type="password" placholder="password"/>
        //              <input type="submit" value='submit'/>
        //          </form>
        //          <p>Or <Link to="/Signup">register</Link></p>
        //   </div> 
        )

    }
}

export default Signin
